Если НЕ Фокус_ПолучитьЗначение(ИНН,"req","UL") = Неопределено Тогда

	// юр. лицо
	СтатусРеорганизация = Фокус_ПолучитьЗначение(ИНН,"req","UL.status.reorganizing");
	СтатусЛиквидация	= Фокус_ПолучитьЗначение(ИНН,"req","UL.status.dissolving");
	СтатусНедействующее	= Фокус_ПолучитьЗначение(ИНН,"req","UL.status.dissolved");

	МаркерСработал = Истина;

	Если СтатусНедействующее = Истина Тогда
		РасшифровкаМаркера = "Недействующее";
	ИначеЕсли СтатусЛиквидация = Истина Тогда
		РасшифровкаМаркера = "В процессе ликвидации (либо планируется исключени из ЕГРЮЛ)";
	ИначеЕсли СтатусРеорганизация = Истина Тогда
		РасшифровкаМаркера = "В процессе реорганизации (может прекратить деятельность в результате реорганизации)";
	Иначе
		МаркерСработал = Ложь;
	КонецЕсли;

ИначеЕсли НЕ Фокус_ПолучитьЗначение(ИНН,"req","IP") = Неопределено Тогда

	// ИП

	СтатусНедействующее	= Фокус_ПолучитьЗначение(ИНН,"req","IP.status.dissolved");

	Если СтатусНедействующее = Истина Тогда
		МаркерСработал = Истина;
		РасшифровкаМаркера = "Недействующее";
	КонецЕсли;

КонецЕсли;

Если НЕ Фокус_ПолучитьЗначение(ИНН,"req","UL") = Неопределено Тогда

	МаркерСработал = Истина;

	Если Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.m7016") = Истина Тогда
	    РасшифровкаМаркера = "Обнаружены признаки завершенной процедуры банкротства";
	ИначеЕсли Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.m7014") = Истина Тогда
	    РасшифровкаМаркера = "Обнаружены сообщения о банкротстве за последние 12 месяцев";
	ИначеЕсли Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.m7013") = Истина Тогда
	    РасшифровкаМаркера = "Обнаружены сообщения о банкротстве за последние 3 месяца";
	Иначе
