СуммаДел		= Фокус_ПолучитьЗначение(ИНН,"analytics",	"analytics.s1001");
Выручка			= Фокус_ПолучитьЗначение(ИНН,"analytics",	"analytics.s6003");
УставныйКапитал	= Фокус_ПолучитьЗначение(ИНН,"egrDetails",	"UL.statedCapital.sum");

Если ЗначениеЗаполнено(СуммаДел) И ЗначениеЗаполнено(Выручка) Тогда

	УсловиеУставный = Истина;
	Если ЗначениеЗаполнено(УставныйКапитал) Тогда
		Если СуммаДел <= УставныйКапитал Тогда
			УсловиеУставный = Ложь;
		КонецЕсли;
	КонецЕсли;

	Если СуммаДел > Выручка*0.2 И СуммаДел > 100000 И УсловиеУставный Тогда
		МаркерСработал = Истина;
		РасшифровкаМаркера = "Сумма дел: " + Фокус_Сумма(СуммаДел) + ". Выручка: " + Фокус_Сумма(Выручка);
		Если ЗначениеЗаполнено(УставныйКапитал) Тогда
        	РасшифровкаМаркера = РасшифровкаМаркера + ". Уставный капитал: " + Фокус_Сумма(УставныйКапитал);
		КонецЕсли;
	КонецЕсли;

КонецЕсли;

СуммаДел		= Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.s2003");
СуммаДелПрошлое	= Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.s2004");

Если ЗначениеЗаполнено(СуммаДел) И ЗначениеЗаполнено(СуммаДелПрошлое) Тогда
	Если СуммаДел>1000000 Тогда
		СреднееЗаПредыдущиеГода = (СуммаДелПрошлое-СуммаДел)/2;
		Если НЕ СреднееЗаПредыдущиеГода = 0 Тогда
			Если СуммаДел > (1.4*СреднееЗаПредыдущиеГода) Тогда
				РостСумма = СуммаДел-СреднееЗаПредыдущиеГода;
				Если РостСумма > 500000 Тогда
					МаркерСработал = Истина;
					РасшифровкаМаркера = "Сумма дел: " + Фокус_Сумма(СуммаДел) + ". Среднее за предыдущие два года: " + Фокус_Сумма(СреднееЗаПредыдущиеГода)+Символы.ПС+
					Фокус_ИзменениеЧисел(СуммаДел,СреднееЗаПредыдущиеГода);
				КонецЕсли;
			КонецЕсли;
		КонецЕсли;
	КонецЕсли;
КонецЕсли;

СуммаДел		= Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.s2001");
СуммаДелПрошлое	= Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.s2002");

Если ЗначениеЗаполнено(СуммаДел) И ЗначениеЗаполнено(СуммаДелПрошлое) Тогда

	Если СуммаДел>1000000 Тогда

	СреднееЗаПредыдущиеГода = (СуммаДелПрошлое-СуммаДел)/2;
	Если НЕ СреднееЗаПредыдущиеГода = 0 Тогда
		Если СуммаДел > (1.4*СреднееЗаПредыдущиеГода) Тогда
			РостСумма = СуммаДел-СреднееЗаПредыдущиеГода;
			Если РостСумма > 500000 Тогда
				РостПроцент = 100*(СуммаДел/СреднееЗаПредыдущиеГода)-100;
				МаркерСработал = Истина;
				РасшифровкаМаркера = "Сумма дел: " + Фокус_Сумма(СуммаДел) + ". Среднее за предыдущие два года: " + Фокус_Сумма(СреднееЗаПредыдущиеГода)+Символы.ПС+
				Фокус_ИзменениеЧисел(СуммаДел,СреднееЗаПредыдущиеГода);
			КонецЕсли;
		КонецЕсли;
	КонецЕсли;

	КонецЕсли;
КонецЕсли;

