	СписокРуководителей = Фокус_ПолучитьЗначение(ИНН, "req", "UL.heads");

	Если НЕ СписокРуководителей = Неопределено Тогда

		Для Каждого Эл ИЗ СписокРуководителей Цикл

			ИННФЛ = Фокус_ПолучитьЗначениеПоляДанных(Эл,"innfl");

			Если ЗначениеЗаполнено(ИННФЛ) Тогда

			ДанныеОБанкротстве = Фокус_ПолучитьДанные_БанкротствоФизЛица(ИННФЛ);
			Если ЗначениеЗаполнено(ДанныеОБанкротстве) Тогда
				Если ДанныеОБанкротстве.Количество()>0 Тогда
					МаркерСработал = Истина;
					РасшифровкаМаркера = "Банкрот: "+Фокус_ПолучитьЗначениеПоляДанных(Эл,"fio");
				КонецЕсли;
			КонецЕсли;

			КонецЕсли;

		КонецЦикла;

	КонецЕсли;
КонецЕсли;

