СуммаДелПрошлое	= Фокус_ПолучитьЗначение(ИНН,"analytics","analytics.s2002");

Если ЗначениеЗаполнено(СуммаДел) И ЗначениеЗаполнено(СуммаДелПрошлое) Тогда
	Если СуммаДел > 1000000 Тогда
		СреднееЗаПредыдущиеГода = (СуммаДелПрошлое-СуммаДел)/2;
		Если (СуммаДел > (1.2*СреднееЗаПредыдущиеГода)) И (СуммаДел <= (1.4*СреднееЗаПредыдущиеГода)) Тогда
			Если (СуммаДел - СреднееЗаПредыдущиеГода)>500000 Тогда
				МаркерСработал = Истина;
				РасшифровкаМаркера = "Сумма дел: " + Фокус_Сумма(СуммаДел) + ". Среднее за предыдущие два года: " + Фокус_Сумма(СреднееЗаПредыдущиеГода) + Символы.ПС + 
