Если НЕ Фокус_ПолучитьЗначение(ИНН,"req","UL") = Неопределено Тогда

	ДатаОбразования = Фокус_ПолучитьЗначение(ИНН,"req","UL.registrationDate");

Иначе

	ДатаОбразования = Фокус_ПолучитьЗначение(ИНН,"req","IP.registrationDate");

КонецЕсли;

Если ЗначениеЗаполнено(ДатаОбразования) Тогда

	Если ТипЗнч(ДатаОбразования) = Тип("Строка") Тогда
		Если СтрДлина(ДатаОбразования) = 10 Тогда
			Год = Лев(ДатаОбразования,4);
			Месяц = Сред(ДатаОбразования,6,2);
			День = Прав(ДатаОбразования,2);

			ДатаОбразования = Дата(Год,Месяц,День);

			Если (ДатаОбразования + 1825*24*3600) < ТекущаяДата() Тогда

				МаркерСработал = Истина;
				РасшифровкаМаркера = "Дата образования: "+Формат(ДатаОбразования,"ДФ=dd.MM.yyyy");

			КонецЕсли;
		КонецЕсли;
	КонецЕсли;
КонецЕсли;

Если НЕ Фокус_ПолучитьЗначение(ИНН,"req","UL") = Неопределено Тогда

	ПредыдущееЗначение = Фокус_ПолучитьПредыдущееЗначение(ИНН,"req","UL.legalName.short");

	Если НЕ ПредыдущееЗначение = Неопределено Тогда

		ТекущееЗначение = Фокус_ПолучитьЗначение(ИНН,"req","UL.legalName.short");

		Если НЕ ТекущееЗначение = ПредыдущееЗначение Тогда
			МаркерСработал = Истина;
			ВидМаркера = "Разовый"; // чтобы не срабатывало исчезновение маркера
			РасшифровкаМаркера = "Новое наименование: " + ТекущееЗначение + Символы.ПС + "Старое наименование: " + ПредыдущееЗначение;
		КонецЕсли;
	КонецЕсли;
КонецЕсли;

Если НЕ Фокус_ПолучитьЗначение(ИНН,"req","UL") = Неопределено Тогда

	ПредыдущееЗначение = Фокус_ПолучитьПредыдущееЗначение(ИНН,"req","UL.legalName.full");

	Если НЕ ПредыдущееЗначение = Неопределено Тогда

		ТекущееЗначение = Фокус_ПолучитьЗначение(ИНН,"req","UL.legalName.full");

		Если НЕ ТекущееЗначение = ПредыдущееЗначение Тогда
			МаркерСработал = Истина;
			ВидМаркера = "Разовый"; // чтобы не срабатывало исчезновение маркера
